import {
  Avatar,
  Dropdown,
  DropdownDivider,
  DropdownHeader,
  DropdownItem,
  Navbar,
  NavbarBrand,
  NavbarCollapse,
  NavbarLink,
  NavbarToggle,
} from "flowbite-react";
import { useState } from "react";
import { FaSearch } from "react-icons/fa";
import { RxCross2 } from "react-icons/rx";
export default function NavBar() {
  const [isSearch, setIsSearch] = useState(false);
  const handleSearch = () => {
    setIsSearch(!isSearch);
  };
  return (
    <Navbar fluid rounded>
      <NavbarBrand href="https://flowbite-react.com">
        {/* <img
          src="/favicon.svg"
          className="mr-3 h-6 sm:h-9"
          alt="Flowbite React Logo"
        /> */}
        <span className="self-center whitespace-nowrap text-xl font-semibold dark:text-white">
          Flowbite React
        </span>
      </NavbarBrand>
      <div className="flex md:order-2">
        {isSearch && (
          <div className="relative w-56">
            <input
              type="text"
              className="w-full px-3 py-2 pl-10 text-sm text-gray-900 placeholder-gray-500 bg-white border border-gray-300 rounded-lg focus:outline-none focus:ring focus:ring-blue-400 focus:border-blue-400"
              placeholder="Search..."
            />
            <FaSearch className="absolute w-5 h-5 text-gray-500 left-3 top-2.5" />
          </div>
        )}
        {
          <RxCross2
            className="absolute w-5 h-5 text-gray-500 right-3 top-2.5"
            onClick={handleSearch}
          />
        }
        {!isSearch && (
          <FaSearch
            className="absolute w-5 h-5 text-gray-500 left-0 top-2.5"
            style={{ top: "20px", right: "50px" }}
            onClick={handleSearch}
          />
        )}
        <Dropdown
          arrowIcon={false}
          inline
          label={
            <Avatar
              alt="User settings"
              img="https://flowbite.com/docs/images/people/profile-picture-5.jpg"
              rounded
            />
          }
        >
          <DropdownHeader>
            <span className="block text-sm">Bonnie Green</span>
            <span className="block truncate text-sm font-medium">
              name@flowbite.com
            </span>
          </DropdownHeader>
          <DropdownItem>Dashboard</DropdownItem>
          <DropdownItem>Settings</DropdownItem>
          <DropdownItem>Earnings</DropdownItem>
          <DropdownDivider />
          <DropdownItem>Sign out</DropdownItem>
        </Dropdown>
        <NavbarToggle />
      </div>
      <NavbarCollapse>
        <NavbarLink href="#" active>
          Home
        </NavbarLink>
        <NavbarLink href="#">About</NavbarLink>
        <NavbarLink href="#">Read Books</NavbarLink>
        <NavbarLink href="#">Create Book</NavbarLink>
        <NavbarLink href="#">Contact</NavbarLink>
      </NavbarCollapse>
    </Navbar>
  );
}
