import {
  Avatar,
  Dropdown,
  DropdownDivider,
  DropdownHeader,
  DropdownItem,
  Navbar,
  NavbarBrand,
  NavbarCollapse,
  NavbarLink,
  NavbarToggle,
} from "flowbite-react";
import { useState } from "react";
import { FaSearch } from "react-icons/fa";
import { ImCross } from "react-icons/im";
export default function NavBar() {
  const [isSearch, setIsSearch] = useState(false);
  const handleIsSearch = () => {
    setIsSearch(!isSearch);
  };
  function handleSearch() {
    // Implement your search logic here
  }
  return (
    // <Navbar fluid rounded style={{ backgroundColor: "black" }}>
    //   <NavbarBrand href="#">
    //     {/* <img
    //           src="/favicon.svg"
    //           className="mr-3 h-6 sm:h-9"
    //           alt="Flowbite React Logo"
    //         /> */}
    //     <span className="self-center whitespace-nowrap text-xl font-semibold text-white">
    //       Digi Library
    //     </span>
    //   </NavbarBrand>
    //   <div className="flex md:order-2">
    //     {isSearch && (
    //       <div className="relative w-56" style={{ marginRight: "3vw" }}>
    //         <input
    //           type="text"
    //           className="w-full px-3 py-2 pl-10 text-sm text-white placeholder-gray-500 bg-gray-800 border border-gray-300 rounded-lg focus:outline-none focus:ring focus:ring-white focus:border-white active:border-white"
    //           //   style={{ borderColor: "white" }}
    //           placeholder="Search..."
    //         />
    //         <FaSearch
    //           className="absolute w-5 h-5 text-gray-500 left-3 top-2.5"
    //           style={{ marginLeft: "8px", cursor: "pointer" }}
    //           onClick={handleSearch}
    //         />
    //       </div>
    //     )}
    //     {isSearch && (
    //       <ImCross
    //         className="absolute w-3 h-3 text-gray-500 right-3 top-2.5"
    //         style={{ top: "22px", left: "94vw", cursor: "pointer" }}
    //         onClick={handleIsSearch}
    //       />
    //     )}
    //     {!isSearch && (
    //       <FaSearch
    //         className="absolute w-5 h-5 text-gray-500 left-0 top-2.5"
    //         style={{ top: "20px", left: "93vw", cursor: "pointer" }}
    //         onClick={handleIsSearch}
    //       />
    //     )}
    //     <Dropdown
    //       arrowIcon={false}
    //       inline
    //       label={
    //         <Avatar
    //           alt="User settings"
    //           img="https://flowbite.com/docs/images/people/profile-picture-5.jpg"
    //           rounded
    //         />
    //       }
    //     >
    //       <DropdownHeader>
    //         <span className="block text-sm">Bonnie Green</span>
    //         <span className="block truncate text-sm font-medium">
    //           name@flowbite.com
    //         </span>
    //       </DropdownHeader>
    //       <DropdownItem>Dashboard</DropdownItem>
    //       <DropdownItem>Settings</DropdownItem>
    //       <DropdownItem>Earnings</DropdownItem>
    //       <DropdownDivider />
    //       <DropdownItem>Sign out</DropdownItem>
    //     </Dropdown>
    //     <NavbarToggle />
    //   </div>
    //   <NavbarCollapse>
    //     <NavbarLink href="#" active>
    //       Home
    //     </NavbarLink>
    //     <NavbarLink href="#" active>
    //       About
    //     </NavbarLink>
    //     <NavbarLink href="#" active>
    //       Read Books
    //     </NavbarLink>
    //     <NavbarLink href="#" active>
    //       Create Book
    //     </NavbarLink>
    //     <NavbarLink href="#" active>
    //       Contact
    //     </NavbarLink>
    //   </NavbarCollapse>
    // </Navbar>

    <nav className="bg-white border-gray-200 dark:bg-gray-900">
      <div className="max-w-screen-xl flex flex-wrap items-center justify-between mx-auto p-4">
        <a
          href="https://flowbite.com/"
          className="flex items-center space-x-3 rtl:space-x-reverse"
        >
          <img
            src="https://flowbite.com/docs/images/logo.svg"
            className="h-8"
            alt="Flowbite Logo"
          />
          <span className="self-center text-2xl font-semibold whitespace-nowrap dark:text-white">
            Flowbite
          </span>
        </a>
        <div className="flex items-center md:order-2 space-x-3 md:space-x-0 rtl:space-x-reverse">
          <button
            type="button"
            className="flex text-sm bg-gray-800 rounded-full md:me-0 focus:ring-4 focus:ring-gray-300 dark:focus:ring-gray-600"
            id="user-menu-button"
            aria-expanded="false"
            data-dropdown-toggle="user-dropdown"
            data-dropdown-placement="bottom"
          >
            <span className="sr-only">Open user menu</span>
            <img
              className="w-8 h-8 rounded-full"
              src="/docs/images/people/profile-picture-3.jpg"
              alt="user"
            />
          </button>
          {/* <!-- Dropdown menu --> */}
          <div
            className="z-50 hidden my-4 text-base list-none bg-white divide-y divide-gray-100 rounded-lg shadow dark:bg-gray-700 dark:divide-gray-600"
            id="user-dropdown"
          >
            <div className="px-4 py-3">
              <span className="block text-sm text-gray-900 dark:text-white">
                Bonnie Green
              </span>
              <span className="block text-sm  text-gray-500 truncate dark:text-gray-400">
                name@flowbite.com
              </span>
            </div>
            <ul className="py-2" aria-labelledby="user-menu-button">
              <li>
                <a
                  href="#"
                  className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 dark:hover:bg-gray-600 dark:text-gray-200 dark:hover:text-white"
                >
                  Dashboard
                </a>
              </li>
              <li>
                <a
                  href="#"
                  className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 dark:hover:bg-gray-600 dark:text-gray-200 dark:hover:text-white"
                >
                  Settings
                </a>
              </li>
              <li>
                <a
                  href="#"
                  className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 dark:hover:bg-gray-600 dark:text-gray-200 dark:hover:text-white"
                >
                  Earnings
                </a>
              </li>
              <li>
                <a
                  href="#"
                  className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 dark:hover:bg-gray-600 dark:text-gray-200 dark:hover:text-white"
                >
                  Sign out
                </a>
              </li>
            </ul>
          </div>
          <button
            data-collapse-toggle="navbar-user"
            type="button"
            className="inline-flex items-center p-2 w-10 h-10 justify-center text-sm text-gray-500 rounded-lg md:hidden hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-gray-200 dark:text-gray-400 dark:hover:bg-gray-700 dark:focus:ring-gray-600"
            aria-controls="navbar-user"
            aria-expanded="false"
          >
            <span className="sr-only">Open main menu</span>
            <svg
              className="w-5 h-5"
              aria-hidden="true"
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 17 14"
            >
              <path
                stroke="currentColor"
                stroke-linecap="round"
                stroke-linejoin="round"
                stroke-width="2"
                d="M1 1h15M1 7h15M1 13h15"
              />
            </svg>
          </button>
        </div>
        <div
          className="items-center justify-between hidden w-full md:flex md:w-auto md:order-1"
          id="navbar-user"
        >
